export default function Home({ search = "" }) {
  const navigate = useNavigate();
  const location = useLocation();
  const isAdmin = typeof window !== 'undefined' && localStorage.getItem('isAdmin') === 'true';

  // Estado para el hover del juego seleccionado
  const [hoveredGame, setHoveredGame] = useState(null);
  const [selectedTag, setSelectedTag] = useState("");
  const [hoverTimeout, setHoverTimeout] = useState(null);

  // Funciones para manejar el hover con delay
  const handleMouseEnter = (game) => {
    if (hoverTimeout) {
      clearTimeout(hoverTimeout);
      setHoverTimeout(null);
    }
    setHoveredGame(game);
  };

  const handleMouseLeave = () => {
    const timeout = setTimeout(() => {
      setHoveredGame(null);
    }, 150); // 150ms de delay antes de ocultar
    setHoverTimeout(timeout);
  };

  const handlePanelMouseEnter = () => {
    if (hoverTimeout) {
      clearTimeout(hoverTimeout);
      setHoverTimeout(null);
    }
  };

  const handlePanelMouseLeave = () => {
    const timeout = setTimeout(() => {
      setHoveredGame(null);
    }, 150);
    setHoverTimeout(timeout);
  };

  // Obtener juegos agregados por el admin
  const getCustomGames = () => {
    try {
      const raw = localStorage.getItem('customGames');
      return raw ? JSON.parse(raw) : [];
    } catch {
      return [];
    }
  };

  // Unir lista original y custom
  const [removedIds, setRemovedIds] = useState([]);
  const getAllGames = React.useCallback(() => {
    // Filtra los juegos hardcodeados borrados temporalmente
    const filteredHardcoded = gamesList.filter(g => !removedIds.includes(g.id));
    return [...filteredHardcoded, ...getCustomGames().filter(g => !removedIds.includes(g.id))];
  }, [removedIds]);

  // ===== búsqueda =====
  const [input, setInput] = useState("");
  const [foundGames, setFoundGames] = useState(getAllGames());
  const [tagFilter, setTagFilter] = useState("");
  const handleChange = (e) => {
    const value = e.target.value;
    setInput(value);
    const filtered = getAllGames().filter((g) =>
      g.title.toLowerCase().includes(value.toLowerCase()) &&
      (tagFilter ? g.tags?.includes(tagFilter) : true)
    );
    setFoundGames(filtered);
  };

  const handleTagFilter = (tag) => {
    setTagFilter(tag);
    setInput("");
    const filtered = getAllGames().filter((g) => g.tags?.includes(tag));
    setFoundGames(filtered);
  };

  // Actualizar la lista de juegos cuando removedIds cambie
  useEffect(() => {
    setFoundGames(getAllGames());
  }, [removedIds, getAllGames]);

  // ===== carrito =====
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [toast, setToast] = useState("");

  useEffect(() => {
    try {
      const raw = localStorage.getItem("cart");
      setCart(raw ? JSON.parse(raw) : []);
    } catch {
      setCart([]);
    }
    // Actualizar lista de juegos si se agregan nuevos
    setFoundGames(getAllGames());
  }, []);
  useEffect(() => {
    const reloadCart = () => {
      try {
        const raw = localStorage.getItem("cart");
        setCart(raw ? JSON.parse(raw) : []);
      } catch {}
    };
    const reloadGames = () => {
      setFoundGames(getAllGames());
    };
    window.addEventListener("cart-updated", reloadCart);
    window.addEventListener("focus", reloadCart);
    window.addEventListener("customGames-updated", reloadGames);
    return () => {
      window.removeEventListener("cart-updated", reloadCart);
      window.removeEventListener("focus", reloadCart);
      window.removeEventListener("customGames-updated", reloadGames);
    };
  }, []);

  useEffect(() => {
    if (location.state?.openCart) {
      setShowCart(true);
      navigate(".", { replace: true, state: {} });
    }
  }, [location.state, navigate]);

  // Limpiar timeout al desmontar
  useEffect(() => {
    return () => {
      if (hoverTimeout) {
        clearTimeout(hoverTimeout);
      }
    };
  }, [hoverTimeout]);

  const addToCart = (game) => {
    try {
      const raw = localStorage.getItem("cart");
      const current = raw ? JSON.parse(raw) : [];
      const i = current.findIndex((x) => x.id === game.id);
      // Asegura que la imagen esté correctamente asignada
      const image = game.image || game.mainImage;
      if (i >= 0) {
        current[i] = { ...current[i], qty: (current[i].qty || 1) + 1 };
      } else {
        current.push({
          id: game.id,
          title: game.title,
          price: game.price,
          image,
          qty: 1,
        });
      }
      localStorage.setItem("cart", JSON.stringify(current));
      setCart(current);
      window.dispatchEvent(new CustomEvent("cart-updated"));
      // toast
      setToast(`✅ ${game.title} agregado al carrito`);
      setTimeout(() => setToast(""), 2500);
    } catch (e) {
      console.error(e);
    }
  };

  const removeFromCart = (id) => {
    try {
      const updated = cart.filter((item) => item.id !== id);
      localStorage.setItem("cart", JSON.stringify(updated));
      setCart(updated);
      window.dispatchEvent(new CustomEvent("cart-updated"));
    } catch (e) {
      console.error(e);
    }
  };

  const total = useMemo(
    () =>
      cart.reduce(
        (sum, item) => sum + (Number(item.price) || 0) * (item.qty || 1),
        0
      ),
    [cart]
  );

  // Filtro por prop externa `search`
  const filteredByProp = useMemo(
    () =>
      gamesList.filter((g) =>
        g.title.toLowerCase().includes((search || "").toLowerCase())
      ),
    [search]
  );

  const finalList = (search?.length ? filteredByProp : foundGames) || [];

  return (
  <div className="home-root" style={{ marginTop: 60, background: "linear-gradient(135deg, #1b2838 0%, #171a21 100%)", minHeight: '100vh' }}>
      <style jsx>{`
        .game-list-panel::-webkit-scrollbar {
          width: 8px;
        }
        .game-list-panel::-webkit-scrollbar-track {
          background: #23262e;
        }
        .game-list-panel::-webkit-scrollbar-thumb {
          background: #4b4b4b;
          border-radius: 4px;
        }
        .game-list-panel::-webkit-scrollbar-thumb:hover {
          background: #66c0f4;
        }
      `}</style>
      {/* Toast (sin alert y sin "localhost dice") */}
      {toast && (
        <div
          style={{
            position: "fixed",
            top: 20,
            right: 20,
            background: "#1b2838",
            color: "#66c0f4",
            padding: "12px 20px",
            borderRadius: 8,
            boxShadow: "0 4px 8px rgba(0,0,0,0.3)",
            fontWeight: "bold",
            zIndex: 1000,
          }}
        >
          {toast}
        </div>
      )}

      {/* NavBar tipo Steam */}
      <NavBar
        handleChange={handleChange}
        input={input}
        onCartClick={() => setShowCart(true)}
        cartCount={cart.length}
        onTagFilter={handleTagFilter}
      />

      {/* Carrusel (si lo usan) */}
      {typeof SteamCarousel === "function" && (
          <SteamCarousel addToCart={addToCart} cart={cart} navigate={navigate} />
      )}

     
      {/* Interfaz Steam con dos paneles */}
      <div style={{
        maxWidth: "1400px",
        margin: "32px auto",
        padding: "0 24px",
        display: "flex",
        gap: 0,
        minHeight: "600px"
      }}>
        {/* Panel izquierdo - Lista de juegos */}
        <div style={{
          flex: "1",
          background: '#23262e',
          borderRadius: "12px 0 0 12px",
          padding: 16,
          maxHeight: "700px",
          overflowY: "auto",
          scrollbarWidth: "thin",
          scrollbarColor: "#4b4b4b #23262e"
        }}
        className="game-list-panel"
        >
          <h2 style={{ 
            color: '#66c0f4', 
            fontSize: 20, 
            fontWeight: 600, 
            marginBottom: 16,
            borderBottom: "1px solid #2a475e",
            paddingBottom: 8
          }}>
            Juegos Destacados
          </h2>
          
          {finalList.map((game, index) => (
            <div
              key={game.id}
              onMouseEnter={() => handleMouseEnter(game)}
              onMouseLeave={handleMouseLeave}
              style={{
                display: "flex",
                alignItems: "center",
                padding: 12,
                marginBottom: 8,
                borderRadius: 8,
                background: hoveredGame?.id === game.id ? '#2a475e' : 'transparent',
                cursor: "pointer",
                transition: "all 0.2s ease",
                border: hoveredGame?.id === game.id ? '1px solid #66c0f4' : '1px solid transparent'
              }}
              onClick={() => navigate(`/game/${game.id}`, { state: { game } })}
            >
              {/* Imagen del juego */}
              <img
                src={game.image}
                alt={game.title}
                style={{
                  width: 120,
                  height: 45,
                  objectFit: "cover",
                  borderRadius: 4,
                  marginRight: 12
                }}
              />
              
              {/* Info del juego */}
              <div style={{ flex: 1 }}>
                <div style={{ 
                  color: "#fff", 
                  fontSize: 16, 
                  fontWeight: 500,
                  marginBottom: 4
                }}>
                  {game.title}
                </div>
                
                {/* Precio */}
                <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <span style={{ 
                    color: '#66c0f4', 
                    fontWeight: 600,
                    fontSize: 16
                  }}>
                    ${game.price.toFixed(2)}
                  </span>
                </div>
              </div>

              {/* Botón agregar al carrito */}
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  addToCart(game);
                }}
                disabled={cart.find((item) => item.id === game.id)}
                style={{
                  background: cart.find((item) => item.id === game.id)
                    ? '#4b6479'
                    : '#66c0f4',
                  color: cart.find((item) => item.id === game.id)
                    ? '#acb4bd'
                    : '#171a21',
                  border: 'none',
                  borderRadius: 4,
                  padding: '6px 12px',
                  fontSize: 12,
                  fontWeight: 600,
                  cursor: cart.find((item) => item.id === game.id) ? 'not-allowed' : 'pointer',
                  transition: 'all 0.2s ease'
                }}
              >
                {cart.find((item) => item.id === game.id) ? '✓' : '+'}
              </button>
            </div>
          ))}
        </div>

        {/* Panel derecho - Detalles del juego en hover */}
        <div 
          style={{
            flex: "1",
            background: '#171a21',
            borderRadius: "0 12px 12px 0",
            padding: 24,
            minHeight: 600,
            position: "sticky",
            top: 24
          }}
          onMouseEnter={handlePanelMouseEnter}
          onMouseLeave={handlePanelMouseLeave}
        >
          {hoveredGame ? (
            <div style={{ height: "100%" }}>
              {/* Título y rating */}
              <div style={{ marginBottom: 20 }}>
                <h1 style={{ 
                  color: '#fff', 
                  fontSize: 28, 
                  fontWeight: 700,
                  marginBottom: 8
                }}>
                  {hoveredGame.title}
                </h1>
                
                {/* Rating */}
                <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 12 }}>
                  <div style={{ display: 'flex', alignItems: 'center' }}>
                    {[1, 2, 3, 4, 5].map((star) => (
                      <span
                        key={star}
                        style={{
                          color: star <= Math.floor(hoveredGame.rating) ? '#ffd700' : '#4b4b4b',
                          fontSize: 18
                        }}
                      >
                        ★
                      </span>
                    ))}
                  </div>
                  <span style={{ color: '#c7d5e0', fontSize: 16 }}>
                    {hoveredGame.rating}/5
                  </span>
                </div>

                {/* Tags clickeables */}
                <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap', marginBottom: 16 }}>
                  {hoveredGame.tags.map((tag, idx) => (
                    <button
                      key={idx}
                      onClick={() => setSelectedTag(tag)}
                      style={{
                        background: selectedTag === tag ? '#66c0f4' : '#2a475e',
                        color: selectedTag === tag ? '#171a21' : '#c7d5e0',
                        border: 'none',
                        borderRadius: 20,
                        padding: '6px 12px',
                        fontSize: 13,
                        fontWeight: 500,
                        cursor: 'pointer',
                        transition: 'all 0.2s ease'
                      }}
                    >
                      {tag}
                    </button>
                  ))}
                </div>
              </div>

              {/* Grid de 4 screenshots */}
              <div style={{ marginBottom: 20 }}>
                <h3 style={{ color: '#66c0f4', fontSize: 18, marginBottom: 12 }}>Screenshots</h3>
                <div style={{
                  display: 'grid',
                  gridTemplateColumns: '1fr 1fr',
                  gap: 8,
                  marginBottom: 16
                }}>
                  {hoveredGame.images.map((img, idx) => (
                    <img
                      key={idx}
                      src={img}
                      alt={`Screenshot ${idx + 1}`}
                      style={{
                        width: '100%',
                        height: 120,
                        objectFit: 'cover',
                        borderRadius: 6,
                        transition: 'transform 0.2s ease',
                        cursor: 'pointer'
                      }}
                      onMouseEnter={(e) => e.target.style.transform = 'scale(1.05)'}
                      onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
                    />
                  ))}
                </div>
              </div>

              {/* Descripción */}
              <div style={{ marginBottom: 20 }}>
                <h3 style={{ color: '#66c0f4', fontSize: 18, marginBottom: 12 }}>Descripción</h3>
                <p style={{ 
                  color: '#c7d5e0', 
                  lineHeight: 1.6,
                  fontSize: 14
                }}>
                  {hoveredGame.description}
                </p>
              </div>

              {/* Precio y botón de compra */}
              <div style={{
                position: 'absolute',
                bottom: 24,
                left: 24,
                right: 24,
                background: '#171a21',
                padding: 16,
                borderRadius: 8,
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
              }}>
                <div>
                  <div style={{ 
                    color: '#66c0f4', 
                    fontSize: 24, 
                    fontWeight: 700 
                  }}>
                    ${hoveredGame.price.toFixed(2)}
                  </div>
                </div>
                
                <button
                  onClick={() => addToCart(hoveredGame)}
                  disabled={cart.find((item) => item.id === hoveredGame.id)}
                  style={{
                    background: cart.find((item) => item.id === hoveredGame.id)
                      ? '#4b6479'
                      : 'linear-gradient(90deg, #66c0f4 0%, #417a9b 100%)',
                    color: cart.find((item) => item.id === hoveredGame.id)
                      ? '#acb4bd'
                      : '#171a21',
                    border: 'none',
                    borderRadius: 6,
                    padding: '12px 24px',
                    fontSize: 16,
                    fontWeight: 600,
                    cursor: cart.find((item) => item.id === hoveredGame.id) ? 'not-allowed' : 'pointer',
                    transition: 'all 0.2s ease',
                    minWidth: 150
                  }}
                >
                  {cart.find((item) => item.id === hoveredGame.id) ? 'En el carrito' : 'Agregar al carrito'}
                </button>
              </div>
            </div>
          ) : (
            <div style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              height: '100%',
              color: '#8f98a0',
              textAlign: 'center'
            }}>
              <div style={{ fontSize: 48, marginBottom: 16 }}>🎮</div>
              <h3 style={{ color: '#66c0f4', fontSize: 20, marginBottom: 8 }}>
                Explora nuestros juegos
              </h3>
              <p style={{ fontSize: 16, lineHeight: 1.5 }}>
                Pasa el cursor sobre cualquier juego de la lista para ver sus detalles, 
                screenshots y más información.
              </p>
            </div>
          )}
        </div>
      </div>

      <footer className="footer" style={{ background: "transparent" }}>
        © 2025 Grupo 4. Todos los derechos reservados.
      </footer>
    </div>
  );
}
